def test_basic_stateful_kernel_with_io_aliasing(self):

    def copy_kernel(x_ref, y_ref, x_old_ref, x_old_ref2, sem):
        del x_old_ref, x_old_ref2
        pltpu.make_async_copy(x_ref, y_ref, sem).start()
        pltpu.make_async_copy(x_ref, y_ref, sem).wait()

    def f_stateful(refs):
        x_ref, y_ref, o_ref = refs
        x = pl.pallas_call(functools.partial(copy_kernel, x_ref, y_ref), scratch_shapes=[pltpu.SemaphoreType.DMA], out_shape=jax.ShapeDtypeStruct(x_ref.shape, x_ref.dtype), input_output_aliases={0: 0})(x_ref[...])
        o_ref[...] = x

    @jax.jit
    def f(x):
        _, y, o = pl.run_state(f_stateful)((x, jnp.zeros_like(x), jnp.zeros_like(x)))
        return (y, o)
    x = jnp.arange(8 * 128, dtype=jnp.int32).reshape((8, 128))
    y, o = f(x)
    np.testing.assert_array_equal(y, x)
    np.testing.assert_array_equal(o, x)

